import React from "react";
import { Button, Grid, Paper, Typography } from "@material-ui/core";
import useStyles from "../login/styles";
import { useUserState } from "../context";
import { Link } from "react-router-dom";
import moment from "jalali-moment";

const Account = () => {
  const classes = useStyles();
  const user = useUserState().user;

  const covertToPersianDateTime = (item) => {
    if (item && item !== undefined) {
      return moment
        .utc(moment.utc().format(item))
        .locale("fa")
        .local()
        .format("H:mm YYYY/MM/DD ");
    } else {
      return "";
    }
  };
  // console.log(user);
  return (
    <div className={classes.root}>
        <AppBar position="static" className={classes.appBar}>
        <CustomContainer
          maxWidth=""
          classes={clsx(classes.pageContentWrapper, {
            classOpanMenu: !!opanMenu,
            paddingLayout: !!paddingLayout,
          })}
        >
          <Toolbar variant="dense" className={classes.toolbar}>
            <div className={classes.grow}>
              {menuItems.map((item, index) => (
                <Link
                  key={item.id}
                  to={
                    item.path === "/marketOrder" ? "/marketOrder/buy" : history
                  }
                >
                  <Button
                    onClick={(e) => handlePopoverOpen(e, item.id, item.path)}
                    className={
                      state.market === "/marketOrder"
                        ? classes.marketStyle
                        : history !== "/marketOrder/buy"
                        ? classes.iconButton
                        : item.id === "1"
                        ? classes.marketStyle2
                        : classes.iconButton
                    }
                    aria-label={item.label}
                    color="inherit"
                  >
                    <span className={clsx(item.iconImage, classes.icon2)} />
                    <Typography
                      color={
                        state.openedPopoverId !== item.id
                          ? "secondary.main"
                          : state.market === "/marketOrder"
                          ? "secondary.main"
                          : "primary.main"
                      }
                      variant={"sm"}
                    >
                      {item.title}
                    </Typography>
                    <span
                      className={clsx(
                        state.openedPopoverId === item.id
                          ? item.closeIcon
                          : item.icon,
                        classes.icon
                      )}
                    />
                  </Button>
                  {item.icon?.length > 0 && item.id === index && (
                    <Popover
                      open={state.openedPopoverId === item.id}
                      anchorEl={state.anchorEl}
                      onClose={handlePopoverClose}
                      className={classes.popOverContainer}
                      anchorOrigin={{
                        vertical: "bottom",
                        horizontal: "right",
                      }}
                      transformOrigin={{
                        vertical: "top",
                        horizontal: "left",
                      }}
                    >
                      <Paper className={classes.popOver} elevation={0}>
                        <item.popOverComponent
                          handlePopoverClose={handlePopoverClose}
                        />
                      </Paper>
                    </Popover>
                  )}
                </Link>
              ))}
            </div>
            <div className={classes.leftSection}>
              {(authorized && (
                <div className={classes.sectionDesktop}>
                  <span className={classes.changeTheme} onClick={toggleTheme}>
                    <i className="icon-sun" />
                  </span>
                  <div className={classes.question}>
                    <i className="icon-question" />
                  </div>
                  <NotificationHeaderBadge />
                  <UserDropdownMenu />
                </div>
              )) || (
                <>
                  <>
                    <Link to={"/register"}>
                      <Button variant={"contained"} color={"secondary.main"}>
                        {__t("signup")}
                      </Button>
                    </Link>
                    <Link to={"/login"}>
                      <Button variant={"outlined"} color={"secondary.main"}>
                        {__t("sign in")}
                      </Button>
                    </Link>
                  </>
                </>
              )}
            </div>
          </Toolbar>
        </CustomContainer>
      </AppBar>
      <Paper elevation={0} className={classes.paper}>
        <div className={classes.content}>
          <div className={classes.image}>
            <img
              src="https://tipaxco.com/UI/Styles/Default/images/logo.svg"
              alt="logo"
              width="50%"
            />
          </div>
          <Typography variant="h5" className={classes.text}>
            {user?.userName} {"عزیز خوش آمدید"}
          </Typography>
        </div>
      </Paper>
    </div>
  );
};

export default Account;
