import React, { useEffect } from "react";
import { StylesProvider } from "@material-ui/styles";
import MainApp from "./app/mainApp";
import CssBaseline from "@material-ui/core/CssBaseline/CssBaseline";
import userApi from "./helper/user";

const App = () => {
  const token = window.localStorage.getItem("access_token");
  useEffect(() => {
    if (!!token) {
      userApi
        .getCurrentUserSummary()
        .then((data) => {})
        .catch((e) => {});
    }
  }, [token]);
  const theme = createMuiTheme({
    fontSize: fontSize,
    spacing: 2,
    direction: "rtl",
    distance: "30px",
    typography: {
      fontFamily: "iranyekan",
    },
    palette: themeColor === "LIGHT" ? { ...paletteLight } : { ...paletteDark },
    overrides:
      themeColor === "LIGHT"
        ? { ...overrides, pickerOverrideLight }
        : { ...overrides, pickerOverrideDark },
  });
  return (
    <div dir="rtl">
      <StylesProvider>
        <CssBaseline />
        <MainApp />
      </StylesProvider>
    </div>
  );
};

export default App;
