import { makeStyles } from "@material-ui/styles";

export default makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
  },
  contentWrapper: {
    height: "100%",
    width: "100%",
    position: "relative",
    "& > div": {
      height: "100%",
    },


  },
  close: {
    fontSize: "24px",
    position: "absolute",
    right: "24px",
    top: "24px",
  },
  bottomContentWrapper: {
    textAlign: "center",
    "&>a": {
      marginLeft: theme.spacing(4),
    },
  },
  logo: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "60px",
    margin: "auto",
    marginBottom: theme.spacing(4),
    "&>img": {
      height: "60px",
    },
  },

  field: {
    textAlign: "center",
  },
  buttonWrapperLogin: {
    margin: theme.spacing(8, 0),
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  buttonWrapper: {
    margin: theme.spacing(4, 0),
    "& > button": {
      width: "100%",
    },
  },
  buttonWrapper2: {
    margin: theme.spacing(10, 0),
    "& button": {
      width: "100%",
      marginBottom: theme.spacing(4),
    },
  },

  title: {
    marginBottom: theme.spacing(25),
  },
  titleReset: {
    marginBottom: theme.spacing(8),
  },
  titleLogin: {
    marginBottom: theme.spacing(25),
    textAlign: "center",
  },
  titleRegister: {
    marginBottom: theme.spacing(8),
  },
  wrapperReset: {
    marginBottom: theme.spacing(25),
  },
  form: {
    marginTop: theme.spacing(8),
  },
  description: {
    display: "inline-block",
    marginBottom: theme.spacing(4),
  },
  termsWrapper: {
    display: "inline-block",
    "& a": {
      margin: theme.spacing(0, 2),
      display: "inline-block",
    },
  },
  paper: {
    padding: theme.spacing(10),
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  image: {
    backgroundRepeat: "no-repeat",
    backgroundColor: theme.palette.background.paper,
    backgroundSize: "cover",
    backgroundPosition: "center",

    "&.signup": {
      backgroundImage: "url(/static/icons/BG-Login/bg-signup.png)",
    },
    "&.signin": {
      backgroundImage: "url(/static/icons/BG-Login/bg-signin.png)",
    },
    "&.forgetpass": {
      backgroundImage: "url(/static/icons/BG-Login/bg-forgetpass.png)",
    },
    "&.resetpass": {
      backgroundImage: "url(/static/icons/BG-Login/bg-new-pass.png)",
    },
  },
  content: {
    maxWidth: "400px",
    minWidth: "400px",
  },
  btn: {
    marginLeft: theme.spacing(2),
  },
  wrapperTitle: {
    display: "flex",
    alignItems: "flex-start",
    marginBottom: theme.spacing(20),
  },
  wrapperContent: {
    paddingLeft: theme.spacing(8),
    "& p": {
      marginBottom: theme.spacing(6),
    },
  },
  titleName: {
    marginBottom: theme.spacing(16),
  },
  wrapperEmial: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    "& p": {
      marginBottom: "0",
    },
    "& button": {
      padding: "0",
    },
  },
  loading: {
    width: "20px !important",
    height: "20px !important",
    marginRight: "10px",
    color: "#fff",
  },
  formControl: {
    display: "block",
    direction: "rtl !important",
    "& > *": {
      width: "100%",
      // height: "42px"
    },
  },


}));
