import React, { useState } from "react";
import { Link } from "react-router-dom";
import useStyles from "./styles";
import { Grid, Typography, Button, TextField, Paper } from "@material-ui/core";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import schema from "./schema";
import authApi from "../helper/auth";
import { useUserDispatch } from "../context/index";
import { notification } from "../helper/notifications";

const LoginForm = () => {
  const classes = useStyles();
  const dispatchUser = useUserDispatch();
  const [loading, setLoading] = useState(false);
  const { register, handleSubmit, errors } = useForm({
    resolver: yupResolver(getValidationSchema(["userName", "password"])),
  });

  const submit = (value) => {
    setLoading(true);
    authApi
      .login({
        payload: value,
      })
      .then((data) => {
        setLoading(false);
        dispatchUser({
          type: "LOGIN_SUCCESS",
          payload: {
            token: data.content.token,
          },
        });
      })
      .catch((error) => {
        setLoading(false);
        notification(
          "Error",
          "danger",
          error?.message ? error.message : "An error occurred on the server"
        );
      });
  };

  return (
    <Paper elevation={0} className={classes.paper}>
      <div className={classes.content}>
        <Link to="/" className={classes.logo}>
          <img src="/static/icons/Logo/Logo-icon.svg" alt="logo" />
        </Link>
        <Typography
          variant="lg"
          color="secondary.main"
          className={classes.titleLogin}
        >
          {"Login to account"}
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit(submit)}>
          <Grid container spacing={8}>
            <>
              <Grid
                item
                className={classes.formControl2}
                {...{ xl: 12, lg: 12, md: 12, xs: 12, sm: 12 }}
              >
                <TextField
                  autoFocus={true}
                  variant="outlined"
                  name={"userName"}
                  inputRef={register}
                  helperText={errors.userName && errors.use.message}
                  error={errors.email}
                  placeholder={"email"}
                  withIconStart={<i className="icon-user-circle"></i>}
                />
              </Grid>
              <Grid
                item
                className={classes.formControl2}
                {...{ xl: 12, lg: 12, md: 12, xs: 12, sm: 12 }}
              >
                <TextField
                  variant="outlined"
                  name={"password"}
                  inputRef={register}
                  helperText={errors.password && errors.password.message}
                  error={errors.password}
                  placeholder={"password"}
                  withIconStart={<i className="icon-key"></i>}
                  withIconEnd={true}
                />
              </Grid>
            </>
          </Grid>
          <div className={classes.buttonWrapperLogin}>
            <Button type="submit" variant="contained" color="primary">
              {/* {loading ? <CircularProgress className={classes.loading} /> : ""} */}
              {"sign in"}
            </Button>

            <Link to={"/forgotPassword"}>
              <Typography variant="xs" color="secondary.tint40">
                {"forgot your password"}
              </Typography>
            </Link>
          </div>

          <div className={classes.bottomContentWrapper}>
            <Typography variant="sm" color="secondary.tint60">
              {"If you do not have an account"}
            </Typography>

            <Link to={"/register"}>
              <Button variant="text" color="teal">
                <Typography variant="sm" color="teal.main">
                  {"register"}
                </Typography>
              </Button>
            </Link>
          </div>
        </form>
      </div>
    </Paper>
  );
};

export default LoginForm;
