import React, { useEffect, useState } from "react";
import Fuse from "fuse.js";
import {
  AppBar,
  Paper,
  TextField,
  Toolbar,
  Typography,
} from "@material-ui/core";
import useStyles from "./styles";
import { useUserState } from "../context";
import curdApi from "../helper/curdApi";
import { notification } from "../helper/notifications";

const Account = () => {
  const classes = useStyles();
  const user = useUserState().user;
  const [province, setProvince] = useState();
  const [provinces, setProvinces] = useState();
  const [search, setSearch] = useState("");
  const fuse = new Fuse(provinces, {
    keys: ["name"],
    includeScore: true,
  });

  const handleChangeSelect = (event) => {
    setProvince(event.target.value);
  };
  useEffect(() => {
    curdApi
      .getProvinces({})
      .then((content) => {
        setProvinces(content.data);
      })
      .catch((data) => {
        notification(
          "",
          "danger",
          !!data && !!data?.message ? data?.message : "SERVER ERROR"
        );
      });
  }, []);

  const handleChange = (e) => {
    const val = fuse.search(e.target.value);
    const results = val.map((character) => character.item.shortName);
    setSearch(results ? results : provinces);
  };
  // console.log(search);
  const values = search ? search : provinces;
  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar variant="dense" className={classes.toolbar}>
          <img
            src="https://tipaxco.com/UI/Styles/Default/images/logo.svg"
            alt="logo"
            width="100px"
          />
          <div className={classes.user}>
            <Typography variant="h5" className={classes.text}>
              {user?.userName}
            </Typography>
          </div>
        </Toolbar>
      </AppBar>
      <Paper elevation={0} className={classes.paper}>
        <div className={classes.content}>
          <div className={classes.image}>
            <TextField
              id="standard-select-currency"
              select
              label="Select"
              value={province}
              onChange={handleChangeSelect}
              helperText="Please select your"
            >
              <TextField
                id="standard-input"
                placeholder={"Search..."}
                onChange={handleChange}
                type="dark"
              />
              currencies.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
              ))}
            </TextField>
          </div>
        </div>
      </Paper>
    </div>
  );
};

export default Account;
