import React, { memo } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import Switch from "../helper/Switch/index";
import Route from "../helper/Route/index";
import { UserProvider } from "@features/User/context";
import PageLayout from "@features/Layout/PageLayout";

const SignInPage = React.lazy(() =>
  import("@features/Registration/pages/SignIn")
);
const SignUpPage = React.lazy(() =>
  import("@features/Registration/pages/SignUp")
);
const ForgotPasswordPage = React.lazy(() =>
  import("@features/Registration/pages/ForgotPassword")
);
const ResetPasswordPage = React.lazy(() =>
  import("@features/Registration/pages/ResetPassword")
);

const Intro = React.lazy(() => import("@features/Dashboard/Intro"));

export const routes = [
  {
    path: "/",
    showHeader: true,
    authorize: true,
    showFooter: true,
    showMenu: true,
    component: Intro,
  },
  {
    path: "/login",
    showHeader: false,
    showFooter: false,
    authorize: false,
    component: SignInPage,
  },
  {
    path: "/register",
    showHeader: false,
    showFooter: false,
    authorize: false,
    component: SignUpPage,
  },
  {
    path: "/forgotPassword",
    showHeader: false,
    showFooter: false,
    authorize: false,
    component: ForgotPasswordPage,
  },
  {
    path: "/resetPassword",
    showHeader: false,
    showFooter: false,
    authorize: false,
    component: ResetPasswordPage,
  },
];

function MainRouter() {
  return (
    <Router>
      <Switch>
        <UserProvider>
          <PageLayout>
            {routes.map(({ component: Component, ...rest }) => (
              <Route exact {...rest} key={rest.path}>
                <Component />
              </Route>
            ))}
          </PageLayout>
        </UserProvider>
      </Switch>
    </Router>
  );
}

export default memo(MainRouter);
