import React, { useEffect, useState } from "react";
import {
  Typography,
  Grid,
  IconButton,
  FormControl,
  OutlinedInput,
  InputAdornment,
  Button,
} from "@material-ui/core";
import useStyles from "./styles";
import CustomTable from "../customTable/CustomTable";
import Fuse from "fuse.js";
import AddBoxIcon from "@material-ui/icons/AddBox";
import EditIcon from "@material-ui/icons/Edit";
import { green } from "@material-ui/core/colors";
import DeleteIcon from "@material-ui/icons/Delete";
import AddModal from "./AddModa";
import EditModal from "./EditModal";
import DeleteModal from "./DeleteModal";
import SearchIcon from "@material-ui/icons/Search";
import clsx from "clsx";
import NewCaseModal from "./NewCaseModal";
import curdApi from "../helper/curdApi";
import { notification } from "../helper/notifications";

const Cites = ({ province, cities, state, setState }) => {
  console.log(province);
  const classes = useStyles();
  const [openAdd, setOpenAdd] = useState(false);
  const [openNew, setOpenNew] = useState(false);
  const [openEdit, setOpenEdit] = useState(false);
  const [openDelete, setOpenDelete] = useState(false);
  const [search, setSearch] = useState();
  const [update, setUpdate] = useState(false);
  const [list, setList] = useState();
  const [searchResult, setSearchResult] = useState();

  useEffect(() => {
    let id = [];
    if (province && province !== undefined) {
      cities?.map((item) => {
        if (province === item.fProvincesId) {
          id.push(item);
        }
      });
      setList(id);
    }
  }, [province, state, update]);
  const handleChangePageNumber = (event, page) => {
    setState((prevState) => ({
      ...prevState,
      pageNumber: page + 1,
      pageSize: state?.pageSize,
    }));
  };
  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  const handleClick2 = () => {
    if (!!search && search !== undefined) {
      const val = fuse2.search(search);
      const results = val.map((character) => character.item.title);
      setSearchResult(results);
    }
  };
  const fuse2 =
    list &&
    new Fuse(list, {
      keys: ["title"],
      includeScore: true,
    });
  const getItem2 = (x) => {
    let res = [];
    x.map((item) => {
      list?.map((elm) => {
        if (item === elm.title) {
          res.push(elm);
        }
      });
    });
    return res;
  };
  const handleDelete = (id) => {
    console.log()
    curdApi
      .getDelete({
        params: {
          id: id,
        },
      })
      .then((data) => {
        notification("", "success", data.message);
      })
      .catch((data) => {
        notification(
          "",
          "danger",
          !!data && !!data?.message ? data?.message : "SERVER ERROR"
        );
      });
  };
  const TableOption = [
    {
      name: "number",
      title: "ردیف",
      date: true,
      render: (record, i, index) => (
        <Typography type="16m" color="textP">
          {index + 1}
        </Typography>
      ),
    },
    {
      name: "name",
      title: "نام شهر",
      date: true,
      render: (record, index) => (
        <div>
          <Typography type="16m" color="textP">
            {record.title}
          </Typography>
        </div>
      ),
    },
    {
      name: "province",
      title: "نام استان",
      render: (record, index) => {
        return (
          <Typography color="textP" type="16m">
            {record.provinceTitle}
          </Typography>
        );
      },
    },
    {
      name: "add",
      title: "اضافه کردن",
      render: (record, index) => (
        <div>
          <IconButton onClick={() => setOpenAdd(true)}>
            <AddBoxIcon style={{ color: green[500] }} />
          </IconButton>
          <AddModal
            open={openAdd}
            setOpenAdd={() => setOpenAdd(false)}
            record={() => record.title}
            onClick={() => handleDelete(record.id)}
            id={console.log(record.id)}
          />
        </div>
      ),
    },
    {
      name: "edit",
      title: "ویرایش کردن",
      render: (record, index) => (
        <div>
          <IconButton onClick={() => setOpenEdit(true)}>
            <EditIcon />
            <EditModal
              open={openEdit}
              handleClose={() => setOpenEdit(false)}
              record={record.title}
              setUpdate={setUpdate}
              id={record.id}
            />
          </IconButton>
        </div>
      ),
    },

    {
      name: "delete",
      title: "حذف کردن",
      render: (record, index) => (
        <div>
          <IconButton onClick={() => setOpenDelete(true)}>
            <DeleteIcon color="secondary" />
          </IconButton>
          <DeleteModal
            open={openDelete}
            record={record.title}
            handleClose={() => setOpenDelete(false)}
            setUpdate={setUpdate}
            id={record.id}
          />
        </div>
      ),
    },
  ];
  return (
    <Grid item xs={12}>
      {province && (
        <div>
          <form className={classes.container}>
            <FormControl className={classes.input3} variant="outlined">
              <OutlinedInput
                id="outlined-adornment"
                placeholder={"جستجو ..."}
                onChange={handleChange}
                value={search}
                endAdornment={
                  <InputAdornment position="end">
                    <IconButton onClick={handleClick2}>
                      <SearchIcon color="disabled" />
                    </IconButton>
                  </InputAdornment>
                }
              />
            </FormControl>
          </form>
          <div className={clsx(classes.buttonWrapperLogin, classes.partB)}>
            <Typography variant="body1" className={classes.text2}>
              {
                "اگر شهر شما در این لیست موجود نمی باشد  دکمه اضافه کن را کلیک کنید"
              }
            </Typography>
            <Button
              variant="contained"
              className={classes.btn}
              onClick={() => setOpenNew(true)}
            >
              {"اضافه کن"}
            </Button>
            <NewCaseModal
              open={openNew}
              handleClose={() => setOpenNew(false)}
              province={province}
              setUpdate={setUpdate}
            />
          </div>
          <CustomTable
            TableOption={TableOption}
            data={searchResult ? getItem2(searchResult) : list}
            is_fetch={state?.is_fetch}
            pageNumber={state?.pageNumber}
            count={state?.count}
            pageSize={state?.pageSize}
            pagination={true}
            handleChange={handleChangePageNumber}
            type="sticky"
          />
        </div>
      )}
    </Grid>
  );
};

export default Cites;
