import React, { useEffect, useContext } from "react";
import { userInitState, userReducer } from "../reducer";
import userApi from "../helper/user";
import { getCachedData } from "@core/utils/localStorageManager";
import { USER } from "@features/User/actions";
import { UpdateSettingContext } from "@context/UpdateSettingProvider";

const UserDispatchContext = React.createContext();
const UserStateContext = React.createContext();

function UserProvider({ children }) {
  const token = getCachedData("access_token");
  const initState = {
    ...userInitState,
    token,
    authorized: !!token ? true : false,
  };

  const [state, dispatch] = React.useReducer(userReducer, initState);

  useEffect(() => {
    if (!!token) {
      userApi
        .getCurrentUserSummary()
        .then(({ content }) => {
          dispatch({
            type: "FETCH_SUCCESS",
            payload: { user: content },
          });
        })
        .catch((e) => {});
    }
  }, [state.token]);

  return (
    <UserStateContext.Provider value={state}>
      <UserDispatchContext.Provider value={dispatch}>
        {children}
      </UserDispatchContext.Provider>
    </UserStateContext.Provider>
  );
}

function useUserState() {
  const context = React.useContext(UserStateContext);
  if (context === undefined) {
    throw new Error("useUserState must be used within a UserProvider");
  }
  return context;
}

function useUserDispatch() {
  const context = React.useContext(UserDispatchContext);
  if (context === undefined) {
    throw new Error("useUserDispatch must be used within a UserProvider");
  }
  return context;
}

export { UserProvider, useUserState, useUserDispatch };
