import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import curdApi from "../helper/curdApi";
import { notification } from "../helper/notifications";
import { FormControl, OutlinedInput, Typography } from "@material-ui/core";
import DeleteIcon from '@material-ui/icons/Delete';
import useStyles from "./styles";

export default function AddModal({ open, handleClose, id }) {
  console.log(id);
  const classes = useStyles();
  const [search, setSearch] = useState();
  const [city, setCity] = useState();
  useEffect(() => {
    if (!!id && id !== undefined) {
      curdApi
        .getId({ payload: {}, id: id })
        .then((content) => {
          setCity(content.data);
        })
        .catch((data) => {
          notification("error", "danger", data?.message || "SERVER ERROR");
        });
    }
  }, [id]);
  const handleChange = (e) => {
    setSearch(e.target.value);
  };
  const handleAdd = () => {};
  const handleEdit = () => {
    console.log(search);
  };
  const handleDelete = () => {
    console.log(search);
  };
  return (
    <div>
      {city && (
        <Dialog open={open} onClose={handleClose}>
          <DialogTitle id="alert-dialog-title">{"خوش آمدید !"}</DialogTitle>
          <DialogContent>
            <form className={classes.formModal}>
              <FormControl className={classes.input2} variant="outlined">
                <OutlinedInput
                  id="outlined-adornment"
                  name={"city"}
                  onChange={handleChange}
                  defaultValue={city?.title}
                />
              </FormControl>
              <Button
                variant="contained"
                onClick={handleEdit}
                color="primary"
                autoFocus
              >
                ویرایش
              </Button>
            </form>
            <div className={classes.text}>
              <Typography variant="body1">
                {"برای حذف شهر کلیک کنید"}
              </Typography>
              <Button
                variant="contained"
                onClick={handleDelete}
                color="secondary"
              >
                {"حذف"}
              </Button>
            </div>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              لغو
            </Button>
          </DialogActions>
        </Dialog>
      )}
    </div>
  );
}
