import makeStyles from "@material-ui/core/styles/makeStyles";
export default makeStyles((theme) => ({
  paper: {
    padding: [theme.spacing(0), "!important"],
    paddingTop: [theme.spacing(0), "!important"],
    boxShadow: "none !important",
  },
  table: {
    minHeight: theme.spacing(60),
  },
  tableHead: {
    "& tr": {
      "& th": {
        color: "#b2b2b2",
        cursor: "default",
        borderBottomColor: "#b2b2b2",
        borderBottomStyle: "solid",
        borderBottomWidth: "thin",
      },
    },
  },
  tableCell: {
    padding: [0, "!important"],
    border: "none",
    [theme.breakpoints.down("sm")]: {
      padding: "5px 10px !important",
    },
    whiteSpace: "nowrap",
  },

  tableRow: {
    cursor: (props) => props.cursorType === "pointer" && "pointer",
    display: "table-row",
    height: theme.spacing(25),
    transition: "0.5s ease",
    "&:hover": {
      transition: "0.5s ease",
      backgroundColor: ["#f5f5f5", "!important"],
      "& td": {
        transition: "0.5s ease",

        "&:first-child": {
          backgroundColor: ["#f5f5f5", "!important"],
        },
        "&:last-child": {
          backgroundColor: ["#f5f5f5", "!important"],
        },
      },
    },
    "&.MuiTableRow-head": {
      background: theme.palette.background.tableRowHead,
      "&:hover": {
        background: [theme.palette.background.tableRowHead, "!important"],
      },
    },

    "& .MuiTableSortLabel-root": {
      flexDirection: "row",
    },

    "& td": {
      "&:first-child": {
        paddingRight: ["20px", "!important"],
      },
    },
    "& th": {
      "&:first-child": {
        paddingRight: ["20px", "!important"],
      },
    },

    [theme.breakpoints.down("sm")]: {
      "& td": {
        "&:first-child": {
          position: "sticky !important",
          right: "0 !important",
          backgroundColor: theme.palette.background.paper,
          boxShadow: "-2px 2px 4px 0 rgba(0,0,0,0.1)",
          zIndex: 1000,
        },
        "&:last-child": {
          position: "sticky !important",
          left: "0 !important",
          backgroundColor: theme.palette.background.paper,
          boxShadow: "2px 2px 4px 0 rgba(0,0,0,0.1)",
          zIndex: 1000,
          maxWidth: ["60px", "!important"],
        },
      },
      "& th": {
        "&:first-child": {
          position: "sticky !important",
          right: "0 !important",
          boxShadow: "-2px 2px 4px 0 rgba(0,0,0,0.1)",
          background: theme.palette.background.tableRowHead,
          zIndex: 1000,
        },
        "&:last-child": {
          position: "sticky !important",
          left: "0 !important",
          boxShadow: "2px 2px 4px 0 rgba(0,0,0,0.1)",
          background: theme.palette.background.tableRowHead,
          zIndex: 1000,
          maxWidth: ["70px", "!important"],
        },
      },
    },
  },
  container: {
    width: "100%",
  },
  pagination: {
    marginTop: theme.spacing(10),
    display: "flex",
    justifyContent: "flex-end",
    "& nav": {
      backgroundColor: "#ffffff",
      borderRadius: "4px",
      boxShadow: theme.palette.shadow1,
      padding: theme.spacing(3, 7),
    },
    "& .MuiPaginationItem-page": {
      border: "none",
      backgroundColor: "inherit",
      color: theme.palette.secondary.tint40,
      width: [theme.spacing(5), "!important"],
      minWidth: [theme.spacing(5), "!important"],
      height: theme.spacing(12),
      margin: theme.spacing(0, 5),
      borderBottom: `3px solid transparent`,
    },
    alignItems: "center",
    "& .Mui-selected": {
      pointerEvents: "none",
      cursor: "no-drop !important",
      backgroundColor: ["#ffffff", "!important"],
      color: "#b2b2b2",
      borderRadius: 0,
      borderBottom: `3px solid ${theme.palette.primary.tint100}`,
    },
    "& li:first-child": {
      display: "block !important",
    },
    "& li:last-child": {
      display: "block !important",
    },
    "& .MuiPaginationItem-icon": {
      transform: "rotate(180deg)",
    },
  },
  seeMore: {
    display: "flex",
    alignItems: "center",
  },
  seeMoreRow: {
    height: theme.spacing(20),
    [theme.breakpoints.down("sm")]: {
      height: theme.spacing(30),
    },
  },
  seeMoreCell: {
    padding: "0px !important",
  },
  lastRowCell: {
    border: "none !important",
  },
}));
