import * as yup from "yup";
const REGEX_PATTERNS = {

  EMAIL:
    /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
  ACCOUNT_NUMBER: /^[0-9 ]{19}$/,
  NATIONAL_CODE1: /^[0-9]{10}$/,
  IBAN_NUMBER: /^[0-9 ]{30}$/,
  ANOUNT_CURRENCY_MIN: /^[0-9 ]{1}$/,
  ANOUNT_CURRENCY_MAX: /^[0-9 ]{5}$/,
};
const userSchemaObject = {
  password: yup
    .string()
    .required(("{field} is Required", { field: "password" }))
    .min(8, "Password is too short - should be 8 chars minimum"),

  email: yup
    .string()
    .matches(REGEX_PATTERNS.EMAIL, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return "{field} is Required", { field: "email" };
      } else if (!REGEX_PATTERNS.EMAIL.test(value)) {
        return "Entered value is not correct";
      }
    }),
  token: yup
    .string()
    .required(("{field} is Required", { field: "Verification code" }))
    .min(5, "Verification code is too short - should be 5 chars minimum"),
};

export default function getUserValidationSchema(arrayOfKeys) {
  const keys =
    arrayOfKeys.length === 0 ? Object.keys(userSchemaObject) : arrayOfKeys;

  const shape = keys.reduce((output, currentKey) => {
    output[currentKey] = userSchemaObject[currentKey];
    return output;
  }, {});

  return yup.object().shape(shape);
}
