import React, { useEffect, useState } from "react";
import {
  AppBar,
  Button,
  MenuItem,
  Paper,
  TextField,
  Toolbar,
  Typography,
  Grid,
  IconButton,
  Dialog,
  FormControl,
  OutlinedInput,
  InputAdornment,
} from "@material-ui/core";
import useStyles from "./styles";
import { useUserState } from "../context";
import curdApi from "../helper/curdApi";
import { notification } from "../helper/notifications";
import CustomTable from "../customTable/CustomTable";
import CustomDialog from "./dialog";
import Fuse from "fuse.js";
import AddBoxIcon from "@material-ui/icons/AddBox";
import EditIcon from "@material-ui/icons/Edit";
import { green } from "@material-ui/core/colors";
import DeleteIcon from "@material-ui/icons/Delete";
import AddModal from "./AddModa";
import EditModal from "./EditModal";
import DeleteModal from "./DeleteModal";
import SearchIcon from "@material-ui/icons/Search";

const Cites = () => {
  const classes = useStyles();
  const user = useUserState().user;
  const [province, setProvince] = useState();
  const [cities, setCities] = useState();
  const [openAdd, setOpenAdd] = useState(false);
  const [openEdit, setOpenEdit] = useState(false);
  const [openDelete, setOpenDelete] = useState(false);
  const [provinces, setProvinces] = useState();
  const [update, setUpdate] = useState(false);
  const [search, setSearch] = useState();
  const [searchResult, setSearchResult] = useState();
  const [state, setState] = useState({
    pageSize: 10,
    pageNumber: 1,
    data: [],
    is_fetch: true,
  });

  const numbers = [
    {
      value: 5,
      id: 0,
    },
    {
      value: 8,
      id: 1,
    },
    {
      value: 10,
      id: 2,
    },
    {
      value: 15,
      label: 3,
    },
  ];
  useEffect(() => {
    curdApi
      .getProvinces({})
      .then((content) => {
        setProvinces(content.data);
      })
      .catch((data) => {
        notification("error", "danger", data?.message || "SERVER ERROR");
      });
    curdApi
      .getCities({})
      .then((content) => {
        setCities(content.data);
      })
      .catch((data) => {
        notification("error", "danger", data?.message || "SERVER ERROR");
      });
  }, []);
  const fuse =
    provinces &&
    new Fuse(provinces, {
      keys: ["title"],
      includeScore: true,
    });
  const handleChangePageSize = (event, page) => {
    setState((prevState) => ({
      ...prevState,
      is_fetch: true,
      pageNumber: 1,
      pageSize: event.target.value,
    }));
    setUpdate(!update);
  };

  const handleChangePageNumber = (event, page) => {
    setState((prevState) => ({
      ...prevState,
      is_fetch: true,
      pageNumber: page - 1,
      pageSize: state.pageSize,
    }));
  };
  const handleChange = (e) => {
    setSearch(e.target.value);
  };
  const handleClick = () => {
    if (!!search && search !== undefined) {
      const val = fuse.search(search);
      const results = val.map((character) => character.item.title);
      setSearchResult(results);
    }
  };
  const handleClick2 = () => {
    if (!!search && search !== undefined) {
      const val = fuse2.search(search);
      const results = val.map((character) => character.item.title);
      setSearchResult(results);
    }
  };
  const getItem = (x) => {
    let res = [];
    x.map((item) => {
      provinces.map((elm) => {
        if (item === elm.title) {
          res.push(elm);
        }
      });
    });
    return res;
  };
  const handleEdit = (e) => {
    setSearch(e.target.value);
  };
  const handleDelete = (e) => {
    setSearch(e.target.value);
  };
  const citiesPro = () => {
    let id = [];
    if (province && province !== undefined) {
      cities?.map((item) => {
        if (province === item.fProvincesId) {
          id.push(item);
        }
        setState((prevState) => ({
          ...prevState,
          is_fetch: true,
          pageNumber: Math.ceil(id.length / state.pageSize),
          pageSize: state.pageSize,
        }));
      });

      return id;
    } else {
      return cities;
    }
  };
  const fuse2 =
    provinces &&
    new Fuse(citiesPro(), {
      keys: ["title"],
      includeScore: true,
    });
  const getItem2 = (x) => {
    let res = [];
    x.map((item) => {
      citiesPro().map((elm) => {
        if (item === elm.title) {
          res.push(elm);
        }
      });
    });
    return res;
  };
  const values = searchResult ? getItem(searchResult) : provinces;

  const TableOption = [
    {
      name: "number",
      title: "ردیف",
      date: true,
      render: (record, i, index) => (
        <Typography type="16m" color="textP">
          {index + 1}
        </Typography>
      ),
    },
    {
      name: "name",
      title: "نام شهر",
      date: true,
      render: (record, index) => (
        <div>
          <Typography type="16m" color="textP">
            {record.title}
          </Typography>
        </div>
      ),
    },
    {
      name: "province",
      title: "نام استان",
      render: (record, index) => {
        return (
          <Typography color="textP" type="16m">
            {record.provinceTitle}
          </Typography>
        );
      },
    },
    {
      name: "add",
      title: "اضافه کردن",
      render: (record, index) => (
        <div>
          <IconButton onClick={() => setOpenAdd(true)}>
            <AddBoxIcon style={{ color: green[500] }} />
            <AddModal
              open={openAdd}
              setOpenAdd={setOpenAdd}
              record={record.title}
              id={record.id}
            />
          </IconButton>
        </div>
      ),
    },
    {
      name: "edit",
      title: "ویرایش کردن",
      render: (record, index) => (
        <div>
          <IconButton onClick={() => setOpenEdit(true)}>
            <EditIcon />
            <EditModal
              open={openEdit}
              handleClose={() => setOpenEdit(false)}
              record={record.title}
              id={record.id}
            />
          </IconButton>
        </div>
      ),
    },

    {
      name: "delete",
      title: "حذف کردن",
      render: (record, index) => (
        <div>
          <IconButton onClick={() => setOpenDelete(true)}>
            <DeleteIcon color="secondary" />
            <DeleteModal
              open={openDelete}
              record={record.title}
              handleClose={() => setOpenDelete(false)}
              id={record.id}
            />
          </IconButton>
        </div>
      ),
    },
  ];
  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar variant="dense" className={classes.toolbar}>
          <img
            src="https://tipaxco.com/UI/Styles/Default/images/logo.svg"
            alt="logo"
            width="100px"
          />
          <div className={classes.user}>
            <Typography variant="h5" className={classes.text}>
              {user?.userName}
            </Typography>
          </div>
        </Toolbar>
      </AppBar>
      <Paper elevation={0} className={classes.paper}>
        <Grid container spacing={3}>
          <Grid item xs={6} className={classes.grid}>
            <div className={classes.content}>
              <CustomDialog
                search={search}
                handleClick={handleClick}
                provinces={values}
                handleChange={handleChange}
                setProvince={setProvince}
              />
            </div>
          </Grid>
          <Grid item xs={6} className={classes.grid}>
            <TextField
              id="standard-select-currency"
              select
              value={state?.pageSize}
              className={classes.input}
              onChange={handleChangePageSize}
              helperText="تعداد هر رکورد را مشخص کنید"
            >
              {numbers.map((option) => (
                <MenuItem
                  key={option.id}
                  value={option.value}
                  className={classes.item}
                >
                  {option.value}
                </MenuItem>
              ))}
            </TextField>
          </Grid>
          <Grid item xs={12}>
            {province && (
              <div>
                <form className={classes.container}>
                  <FormControl className={classes.input3} variant="outlined">
                    <OutlinedInput
                      id="outlined-adornment"
                      placeholder={"جستجو ..."}
                      onChange={handleChange}
                      value={search}
                      endAdornment={
                        <InputAdornment position="end">
                          <IconButton onClick={handleClick2}>
                            <SearchIcon color="disabled" />
                          </IconButton>
                        </InputAdornment>
                      }
                    />
                  </FormControl>
                </form>
                <CustomTable
                  TableOption={TableOption}
                  data={searchResult ? getItem2(searchResult) : citiesPro()}
                  is_fetch={state.is_fetch}
                  pageNumber={state.pageNumber}
                  count={state.count}
                  pageSize={state.pageSize}
                  pagination={true}
                  handleChange={handleChangePageNumber}
                  type="sticky"
                />
              </div>
            )}
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
};

export default Cites;
