import FormControl from "@material-ui/core/FormControl";
import Fuse from "fuse.js";
import { DialogContent, Button, TextField } from "@material-ui/core";
import { useState } from "react";
import useStyles from "./styles";

export default function CountrySelect({ onClick, labels, provinces }) {
  const classes = useStyles();
  const [search, setSearch] = useState("");
  let array = [];
  provinces.map((country) => {
    array.push({ name: labels[country], shortName: country });
  });
  const fuse = new Fuse(array, {
    keys: ["name"],
    includeScore: true,
  });
  const handleChange = (e) => {
    const val = fuse.search(e.target.value);
    const results = val.map((character) => character.item.shortName);
    setSearch(results ? results : provinces);
  };
  // console.log(search);
  const Countries = search ? search : provinces;

  return (
    <DialogContent>
      <form className={classes.container}>
        <TextField
          id="standard-input"
          placeholder={"Search..."}
          onChange={handleChange}
          type="dark"
        />
        <FormControl className={classes.formControl}>
          {Countries.map((country) => (
            <Button
              key={country}
              value={provinces[country]}
              onClick={(e) => onClick(provinces[country])}
              className={classes.selectCountry}
              variant="text"
            >
              <img
                alt={country}
                width={24}
                src={`http://purecatamphetamine.github.io/country-flag-icons/3x2/${country}.svg`}
              />{" "}
              <span>{" " + labels[country]}</span>
              <div> (+{provinces[country]})</div>
            </Button>
          ))}
        </FormControl>
      </form>{" "}
    </DialogContent>
  );
}
