import { cacheData, clearCache } from "@core/utils/localStorageManager";

export const userInitState = {
  authorized: false,
  user: {
    userName: "",
  },
  token: null,
};

export const userReducer = (state = userInitState, action) => {
  switch (action.type) {
    case "LOGIN_SUCCESS":
      cacheData("access_token", action.payload.token);
      return {
        ...state,
        authorized: true,
        token: action.payload.token,
      };

    default:
      return state;
  }
};
