import React, { useEffect, useState } from "react";
import Fuse from "fuse.js";
import {
  AppBar,
  Button,
  MenuItem,
  Paper,
  TextField,
  Toolbar,
  Typography,
} from "@material-ui/core";
import useStyles from "./styles";
import { useUserState } from "../context";
import curdApi from "../helper/curdApi";
import { notification } from "../helper/notifications";
import CustomTable from "../customTable/CustomTable";

const Cites = () => {
  const classes = useStyles();
  const user = useUserState().user;
  const [province, setProvince] = useState();
  const [cities, setCities] = useState();
  const [provinces, setProvinces] = useState();
  const [search, setSearch] = useState();
  const [state, setState] = useState({
    pageSize: 10,
    pageNumber: 0,
    data: [],
    is_fetch: false,
    count: 0,
  });
  const numbers = [
    {
      value: "USD",
      label: "$",
    },
    {
      value: "EUR",
      label: "€",
    },
    {
      value: "BTC",
      label: "฿",
    },
    {
      value: "JPY",
      label: "¥",
    },
  ];
  useEffect(() => {
    curdApi
      .getProvinces({})
      .then((content) => {
        setProvinces(content.data);
      })
      .catch((data) => {
        notification("error", "danger", data?.message || "SERVER ERROR");
      });
    curdApi
      .getCities({})
      .then((content) => {
        setCities(content.data);
      })
      .catch((data) => {
        notification("error", "danger", data?.message || "SERVER ERROR");
      });
  }, []);
  const fuse =
    provinces &&
    new Fuse(provinces, {
      keys: ["title"],
      includeScore: true,
    });
  const handleChangeSelect = (event) => {
    setProvince(event.target.value);
  };
  const handleChangePageNumber = (event, page) => {
    setState((prevState) => ({
      ...prevState,
      is_fetch: false,
      pageNumber: page - 1,
      pageSize: state.pageSize,
    }));
  };
  const handleChange = (e) => {
    const val = fuse.search(e.target.value);
    const results = val.map((character) => character.item.title);
    setSearch(results);
  };
  const values = search ? search : provinces;
  const TableOption = [
    {
      name: "number",
      title: "ردیف",
      date: true,
      render: (record, i, index) => (
        <Typography type="16m" color="textP">
          {index + 1}
        </Typography>
      ),
    },
    {
      name: "name",
      title: "نام شهر",
      date: true,
      render: (record) => (
        <div>
          <Typography type="16m" color="textP">
            {record.title}
          </Typography>
        </div>
      ),
    },
    {
      name: "subscriptionType",
      title: "Subscription Type",
      render: (record) => {
        return (
          <Typography color="textP" type="16m">
            {record.deviceFamily}
          </Typography>
        );
      },
    },
    {
      name: "price",
      title: "Price",
      render: (record) => (
        <div>
          <Typography type="16m" color="textP">
            {"Toman"}
          </Typography>
        </div>
      ),
    },
    {
      name: "startDate",
      title: "Start Date",
      date: true,
      render: (record) => (
        <div>
          <Typography type="16m" color="textP">
            {/* {convertUNIX(record?.createdDate, "YYYY/MM/DD")} */}
          </Typography>
        </div>
      ),
    },

    {
      name: "invoice",
      title: "Invoice",
      date: true,
      render: () => (
        <div>
          <Button href={"/"} color="primaryLight" disabled>
            {"View Invoice"}
          </Button>
        </div>
      ),
    },
  ];
  // console.log(cities);
  // console.log(province);
  const citiesPro = () => {
    let id = [];
    if (province && province !== undefined) {
      cities?.map((item) => {
        if (province === item.fProvincesId) {
          id.push(item);
        }
      });
      return id;
    } else {
      return cities;
    }
  };
  // console.log(citiesPro())
  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar variant="dense" className={classes.toolbar}>
          <img
            src="https://tipaxco.com/UI/Styles/Default/images/logo.svg"
            alt="logo"
            width="100px"
          />
          <div className={classes.user}>
            <Typography variant="h5" className={classes.text}>
              {user?.userName}
            </Typography>
          </div>
        </Toolbar>
      </AppBar>
      <Paper elevation={0} className={classes.paper}>
        <div className={classes.content}>
          <TextField
            id="standard-select-currency"
            select
            label="استان"
            value={province}
            className={classes.input}
            onChange={handleChangeSelect}
            helperText="یک استان را انتخاب کنیذ"
          >
            <TextField
              id="standard-input"
              placeholder={"Search..."}
              onChange={handleChange}
            />
            {values?.map((option) => (
              <MenuItem key={option.id} value={option.id}>
                {option.title}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            id="standard-select-currency"
            select
            label="Select"
            value={state.pageNumber}
            onChange={handleChange}
            helperText="تعداد هر رکورد را مشخص کنید"
          >
            {numbers.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
        </div>

        {province && (
          <div>
            <CustomTable
              TableOption={TableOption}
              data={citiesPro()}
              is_fetch={state.is_fetch}
              pageNumber={state.pageNumber}
              count={state.count}
              pageSize={state.pageSize}
              pagination={true}
              handleChange={handleChangePageNumber}
              type="sticky"
            />
          </div>
        )}
      </Paper>
    </div>
  );
};

export default Cites;
