import React from "react";
import {
  AppBar,
  Button,
  Grid,
  Paper,
  Toolbar,
  Typography,
} from "@material-ui/core";
import useStyles from "../login/styles";
import { useUserState } from "../context";
import { Link } from "react-router-dom";
import moment from "jalali-moment";

const Account = () => {
  const classes = useStyles();
  const user = useUserState().user;

  const covertToPersianDateTime = (item) => {
    if (item && item !== undefined) {
      return moment
        .utc(moment.utc().format(item))
        .locale("fa")
        .local()
        .format("H:mm YYYY/MM/DD ");
    } else {
      return "";
    }
  };
  // console.log(user);
  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar variant="dense" className={classes.toolbar}>
          <div className={classes.leftSection}>
            {(authorized && (
              <div className={classes.sectionDesktop}>
                <span className={classes.changeTheme} onClick={toggleTheme}>
                  <i className="icon-sun" />
                </span>
                <div className={classes.question}>
                  <i className="icon-question" />
                </div>
                <NotificationHeaderBadge />
                <UserDropdownMenu />
              </div>
            )) || (
              <>
                <>
                  <Link to={"/register"}>
                    <Button variant={"contained"} color={"secondary.main"}>
                      {__t("signup")}
                    </Button>
                  </Link>
                  <Link to={"/login"}>
                    <Button variant={"outlined"} color={"secondary.main"}>
                      {__t("sign in")}
                    </Button>
                  </Link>
                </>
              </>
            )}
          </div>
        </Toolbar>
      </AppBar>
      <Paper elevation={0} className={classes.paper}>
        <div className={classes.content}>
          <div className={classes.image}>
            <img
              src="https://tipaxco.com/UI/Styles/Default/images/logo.svg"
              alt="logo"
              width="50%"
            />
          </div>
          <Typography variant="h5" className={classes.text}>
            {user?.userName} {"عزیز خوش آمدید"}
          </Typography>
        </div>
      </Paper>
    </div>
  );
};

export default Account;
