import React from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { Pagination } from "@material-ui/lab";
import { Grid, Paper } from "@material-ui/core";
import useStyles from "./styles";

const CustomTableWithPagination = (props) => {
  let {
    className,
    TableOption,
    data,
    is_fetch,
    handleChange,
    pageNumber,
    count,
    pageSize,
    pagination,
    handleClick,
    paper = true,
    stickyHeader = false,
  } = props;
  console.log(values2);
  const classes = useStyles(props);

  const getCellContent = (row, col, index) => {
    if (col?.render) {
      return col?.render(row, col?.name, index);
    } else {
      return row[col?.name];
    }
  };

  const drawTable = () => {
    return (
      <Grid container spacing={0} direction="column">
        <Grid item className={classes.container}>
          {is_fetch ? (
            data?.length > 0 ? (
              <div className={className}>
                <TableContainer>
                  <Table stickyHeader={stickyHeader} className={classes.table}>
                    <colgroup>
                      {TableOption?.map((item) => (
                        <>
                          <col style={{ width: item?.width }} />
                        </>
                      ))}
                    </colgroup>
                    <TableHead className={classes.tableHead}>
                      <TableRow className={classes.tableRow}>
                        {TableOption?.map((item) => (
                          <TableCell
                            key={item.id}
                            className={classes.tableCell}
                            align={item.align ? item.align : "center"}
                          >
                            {item.title}
                          </TableCell>
                        ))}
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {data?.map((row, index) => (
                        <TableRow
                          key={row?.id}
                          id={row?.currencyName && row?.currencyName}
                          className={classes.tableRow}
                          onClick={handleClick}
                        >
                          {TableOption?.map((item) => (
                            <TableCell
                              className={classes.tableCell}
                              align={item?.align ? item?.align : "center"}
                            >
                              {getCellContent(row, item, index)}
                            </TableCell>
                          ))}
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
                {count && pageSize && count > pageSize && pagination && (
                  <div className={classes.pagination}>
                    <Pagination
                      count={Math.ceil(count / pageSize)}
                      onChange={handleChange}
                      variant="outlined"
                      page={pageNumber + 1}
                    />
                  </div>
                )}
              </div>
            ) : (
              <span>{"There is no data to display"}</span>
            )
          ) : (
            <span>Loading ...</span>
          )}
        </Grid>
      </Grid>
    );
  };

  return paper ? (
    <Paper className={classes.paper}>{drawTable()}</Paper>
  ) : (
    drawTable()
  );
};

export default CustomTableWithPagination;
