import * as yup from "yup";

const userSchemaObject = {
  oldPassword: yup
    .string()
    .required(("{field} is Required", { field: "oldPassword" }))
    .min(8, "Password is too short - should be 8 chars minimum"),

  password: yup
    .string()
    .required(("{field} is Required", { field: "password" }))
    .min(8, "Password is too short - should be 8 chars minimum"),

  repeatPassword: yup
    .string()
    .required(("{field} is Required", { field: "Repeat Password" }))
    .oneOf([yup.ref("password"), null], "Passwords must match"),

  confirm: yup
    .string()
    .required(("{field} is Required", { field: "Repeat Password" }))
    .oneOf([yup.ref("password"), null], "Passwords must match"),

  email: yup
    .string()
    .matches(REGEX_PATTERNS.EMAIL, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return "{field} is Required", { field: "email" };
      } else if (!REGEX_PATTERNS.EMAIL.test(value)) {
        return "Entered value is not correct";
      }
    }),
  token: yup
    .string()
    .required(("{field} is Required", { field: "Verification code" }))
    .min(5, "Verification code is too short - should be 5 chars minimum"),
  totp: yup
    .string()
    .required(("{field} is Required", { field: "google authenticator" }))
    .length(6, "Google authenticator should be 6 chars")
    .matches(/^[0-9]+$/gi, "You must specify a number"),
  firstName: yup
    .string()
    .required(("{field} is Required", { field: "First Name" })),
  lastName: yup
    .string()
    .required(("{field} is Required", { field: "Last Name" })),
  nationalCode: yup.string(),
  // .required(("{field} is Required", { field: ("National Code") }))
  // .length(10, ("National Code must be 10 charactor"))
  // .matches(/^[0-9]*$/gi, ("Enter number only")),

  gender: yup.string().required(("{field} is Required", { field: "gender" })),
  phonenumber: yup
    .string()
    .required(("{field} is Required", { field: "phone number" })),
  accountNumber: yup
    .string()
    .matches(REGEX_PATTERNS.ACCOUNT_NUMBER, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return "{field} is Required", { field: "account number" };
      } else if (!REGEX_PATTERNS.ACCOUNT_NUMBER.test(value)) {
        return "account number must be 16 charactor";
      }
    }),
  IbanNumber: yup
    .string()
    .matches(REGEX_PATTERNS.IBAN_NUMBER, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return "{field} is Required", { field: "Iban number" };
      } else if (!REGEX_PATTERNS.ACCOUNT_NUMBER.test(value)) {
        return "Iban number must be 24 charactor";
      }
    }),
  dateOfBirth: yup
    .date()
    .max(
      moment().endOf("day").subtract(18, "years"),
      "Age Should be greather than 18"
    ),
  withdrawAmount: yup
    .string()
    // .matches(/^[0-9]+$/gi, ("You must specify a number"))
    .required(("{field} is Required", { field: "withdrawal amount" })),
  depositAmount: yup
    .string()
    // .matches(/^[0-9]+$/gi, ("You must specify a number"))
    .required(("{field} is Required", { field: "Deposit Amount" })),
  destWalletAddress: yup
    .string()
    .required(("{field} is Required", { field: "Destination Wallet" })),
  withdrawCoinAmount: yup
    .string()
    // .matches(/^[0-9]+$/gi, ("You must specify a number"))
    .required(("{field} is Required", { field: "Withdraw Amount" })),

  walletName: yup
    .string()
    .required(("{field} is Required", { field: "Wallet Name" })),
  walletAddress: yup
    .string()
    .required(("{field} is Required", { field: "Wallet Address" })),
  tagMemo: yup
    .string()
    .matches(/^[0-9]+$/gi, "You must specify a number")
    .required(("{field} is Required", { field: "Tag Memo" })),
  verificationCode: yup
    .string()
    .matches(/^[0-9]+$/gi, "You must specify a number")
    .typeError("You must specify a number")
    .required(("{field} is Required", { field: "google authunticator" }))
    .min(
      6,
      "Google authunticator code is too short - should be 6 chars minimum"
    ),

  confirmCode: yup
    .string()
    .typeError("You must specify a number")
    .required(("{field} is Required", { field: "google authunticator" }))
    .min(
      6,
      "Google authunticator code is too short - should be 6 chars minimum"
    ),
  securityCode: yup
    .string()
    .required(("{field} is Required", { field: "Security Code" })),
  destTag: yup
    .string()
    .required(("{field} is Required", { field: "Destination Tag" })),
};

export default function getUserValidationSchema(arrayOfKeys) {
  const keys =
    arrayOfKeys.length === 0 ? Object.keys(userSchemaObject) : arrayOfKeys;

  const shape = keys.reduce((output, currentKey) => {
    output[currentKey] = userSchemaObject[currentKey];
    return output;
  }, {});

  return yup.object().shape(shape);
}
