import * as yup from "yup";
import moment from "moment";
const userSchemaObject = {
  oldPassword: yup
    .string()
    .required(__t("{field} is Required", { field: __t("oldPassword") }))
    .min(8, __t("Password is too short - should be 8 chars minimum")),

  password: yup
    .string()
    .required(__t("{field} is Required", { field: __t("password") }))
    .min(8, __t("Password is too short - should be 8 chars minimum")),

  repeatPassword: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Repeat Password") }))
    .oneOf([yup.ref("password"), null], __t("Passwords must match")),

  confirm: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Repeat Password") }))
    .oneOf([yup.ref("password"), null], __t("Passwords must match")),

  email: yup
    .string()
    .matches(REGEX_PATTERNS.EMAIL, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return __t("{field} is Required", { field: __t("email") });
      } else if (!REGEX_PATTERNS.EMAIL.test(value)) {
        return __t("Entered value is not correct");
      }
    }),
  token: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Verification code") }))
    .min(5, __t("Verification code is too short - should be 5 chars minimum")),
  totp: yup
    .string()
    .required(
      __t("{field} is Required", { field: __t("google authenticator") })
    )
    .length(6, __t("Google authenticator should be 6 chars"))
    .matches(/^[0-9]+$/gi, __t("You must specify a number")),
  firstName: yup
    .string()
    .required(__t("{field} is Required", { field: __t("First Name") })),
  lastName: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Last Name") })),
  nationalCode: yup.string(),
  // .required(__t("{field} is Required", { field: __t("National Code") }))
  // .length(10, __t("National Code must be 10 charactor"))
  // .matches(/^[0-9]*$/gi, __t("Enter number only")),

  gender: yup
    .string()
    .required(__t("{field} is Required", { field: __t("gender") })),
  phonenumber: yup
    .string()
    .required(__t("{field} is Required", { field: __t("phone number") })),
  accountNumber: yup
    .string()
    .matches(REGEX_PATTERNS.ACCOUNT_NUMBER, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return __t("{field} is Required", { field: __t("account number") });
      } else if (!REGEX_PATTERNS.ACCOUNT_NUMBER.test(value)) {
        return __t("account number must be 16 charactor");
      }
    }),
  IbanNumber: yup
    .string()
    .matches(REGEX_PATTERNS.IBAN_NUMBER, ({ originalValue, value }) => {
      if (!!originalValue === false) {
        return __t("{field} is Required", { field: __t("Iban number") });
      } else if (!REGEX_PATTERNS.ACCOUNT_NUMBER.test(value)) {
        return __t("Iban number must be 24 charactor");
      }
    }),
  dateOfBirth: yup
    .date()
    .max(
      moment().endOf("day").subtract(18, "years"),
      __t("Age Should be greather than 18")
    ),
  withdrawAmount: yup
    .string()
    // .matches(/^[0-9]+$/gi, __t("You must specify a number"))
    .required(__t("{field} is Required", { field: __t("withdrawal amount") })),
  depositAmount: yup
    .string()
    // .matches(/^[0-9]+$/gi, __t("You must specify a number"))
    .required(__t("{field} is Required", { field: __t("Deposit Amount") })),
  destWalletAddress: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Destination Wallet") })),
  withdrawCoinAmount: yup
    .string()
    // .matches(/^[0-9]+$/gi, __t("You must specify a number"))
    .required(__t("{field} is Required", { field: __t("Withdraw Amount") })),

  walletName: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Wallet Name") })),
  walletAddress: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Wallet Address") })),
  tagMemo: yup
    .string()
    .matches(/^[0-9]+$/gi, __t("You must specify a number"))
    .required(__t("{field} is Required", { field: __t("Tag Memo") })),
  verificationCode: yup
    .string()
    .matches(/^[0-9]+$/gi, __t("You must specify a number"))
    .typeError(__t("You must specify a number"))
    .required(
      __t("{field} is Required", { field: __t("google authunticator") })
    )
    .min(
      6,
      __t("Google authunticator code is too short - should be 6 chars minimum")
    ),

  confirmCode: yup
    .string()
    .typeError(__t("You must specify a number"))
    .required(
      __t("{field} is Required", { field: __t("google authunticator") })
    )
    .min(
      6,
      __t("Google authunticator code is too short - should be 6 chars minimum")
    ),
  securityCode: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Security Code") })),
  destTag: yup
    .string()
    .required(__t("{field} is Required", { field: __t("Destination Tag") }))
};

export default function getUserValidationSchema(arrayOfKeys) {
  const keys =
    arrayOfKeys.length === 0 ? Object.keys(userSchemaObject) : arrayOfKeys;

  const shape = keys.reduce((output, currentKey) => {
    output[currentKey] = userSchemaObject[currentKey];
    return output;
  }, {});

  return yup.object().shape(shape);
}
