import { makeStyles } from "@material-ui/styles";

export default makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
  },
  contentWrapper: {
    height: "100%",
    width: "100%",
    position: "relative",
    "& > div": {
      height: "100%",
    },
  },
  close: {
    fontSize: "24px",
    position: "absolute",
    right: "24px",
    top: "24px",
  },
  bottomContentWrapper: {
    textAlign: "center",
    "&>a": {
      marginLeft: 40,
    },
  },
  logo: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "60px",
    margin: "auto",
    marginBottom: 40,
    "&>img": {
      height: "60px",
    },
  },

  field: {
    textAlign: "center",
  },
  buttonWrapperLogin: {
    margin: "",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  buttonWrapper: {
    margin: (4, 0),
    "& > button": {
      width: "100%",
    },
  },
  buttonWrapper2: {
    margin: (10, 0),
    "& button": {
      width: "100%",
      marginBottom: 40,
    },
  },

  title: {
    marginBottom: 25,
  },
  titleReset: {
    marginBottom: 8,
  },
  titleLogin: {
    marginBottom: 25,
    textAlign: "center",

  },
  titleRegister: {
    marginBottom: 8,
  },
  wrapperReset: {
    marginBottom: 25,
  },
  form: {
    marginTop: 8,
  },
  description: {
    display: "inline-block",
    marginBottom: 40,
  },
  termsWrapper: {
    display: "inline-block",
    "& a": {
      margin: (0, 2),
      display: "inline-block",
    },
  },
  paper: {
    padding: 10,
    height: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  image: {
    backgroundRepeat: "no-repeat",
    backgroundColor: "white",
    backgroundSize: "cover",
    backgroundPosition: "center",
    "&.signup": {
      backgroundImage: "url(/static/icons/BG-Login/bg-signup.png)",
    },
  },
  content: {
    maxWidth: "400px",
    minWidth: "400px",
    backgroundColor: "#33936E",
  },
  btn: {
    marginLeft: 2,
  },
  wrapperTitle: {
    display: "flex",
    alignItems: "flex-start",
    marginBottom: 20,
  },
  wrapperContent: {
    paddingLeft: 8,
    "& p": {
      marginBottom: 6,
    },
  },
  titleName: {
    marginBottom: 16,
  },
  wrapperEmial: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    "& p": {
      marginBottom: "0",
    },
    "& button": {
      padding: "0",
    },
  },
  loading: {
    width: "20px !important",
    height: "20px !important",
    marginRight: "10px",
    color: "#fff",
  },
  formControl: {
    display: "block",
    direction: "rtl !important",
    "& > *": {
      width: "100%",
      // height: "42px"
    },
  },
}));
